 //Generated by CoasterFan5
  const baseUrl = "/v1/users/self/favorites/groups/{id}";
  import type {Favorite} from "../models/Favorite"


  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const add_group_to_favorites = async (token: string, baseUrl: string, params: {
  "id": string
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as Favorite
}
