 //Generated by CoasterFan5
  const baseUrl = "/v1/courses/{course_id}/users/{user_id}/last_attended";
  import type {Enrollment} from "../models/Enrollment"


  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const add_last_attended_date = async (token: string, baseUrl: string, params: {
  "course_id": string;"user_id": string;"date"?: undefined
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as Enrollment
}
