 //Generated by CoasterFan5
  const baseUrl = "/v1/users/self/bookmarks";
  import type {Bookmark} from "../models/Bookmark"


  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const create_bookmark = async (token: string, baseUrl: string, params: {
  "name"?: string;"url"?: string;"position"?: number;"data"?: string
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as Bookmark
}
