  const baseUrl = "/v1/error_reports";
  


  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const create_error_report = async (token: string, baseUrl: string, params: {
  "error[subject]": string;"error[url]"?: string;"error[email]"?: string;"error[comments]"?: string;"error[http_env]"?: undefined
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as void
}
