  const baseUrl = "/v1/jwts";
  import type {JWT} from "../models/JWT"

  const method = "POST"

  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const create_jwt = async (token: string, baseUrl: string, params: {
  "workflows"?: unknown;"context_type"?: string;"context_id"?: number;"context_uuid"?: string;"canvas_audience"?: boolean
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    method: method,
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as JWT
}
