  const baseUrl = "/v1/planner_notes";
  import type {PlannerNote} from "../models/PlannerNote"


  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const create_planner_note = async (token: string, baseUrl: string, params: {
  "title"?: string;"details"?: string;"todo_date"?: undefined;"course_id"?: number;"linked_object_type"?: string;"linked_object_id"?: number
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as PlannerNote
}
