

//IMPORTS
import type {CustomColumn} from "../models/CustomColumn";
const baseUrl = "/v1/courses/{course_id}/custom_gradebook_columns/{id}";
const method = "DELETE";

const urlBuilder = (base: string, items: Record<string, string>) => {
  let newUrl = baseUrl;
  for (const key in items) {
    newUrl = newUrl.replaceAll(`{${key}}`, items[key]);
  }

  return base + newUrl;
};

export const delete_custom_gradebook_column = async (
  token: string,
  baseUrl: string,
  params: {"course_id": string;"id": string},
) => {
  const r = await fetch(urlBuilder(baseUrl, params) + "?per_page=100", {
    method: method,
    headers: {
      Authorization: "Bearer " + token,
    },
  });
  if (r.status != 200) {
    throw r;
  }
  return (await r.json()) as CustomColumn;
};
