  const baseUrl = "/v1/courses/{course_id}/quizzes/{id}";
  import type {Quiz} from "../models/Quiz"

  const method = "PUT"

  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const edit_quiz = async (token: string, baseUrl: string, params: {
  "course_id": string;"id": string;"quiz[notify_of_update]"?: boolean
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    method: method,
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as Quiz
}
