

//IMPORTS
;
const baseUrl = "/v1/courses/{course_id}/discussion_topics/{topic_id}/summaries";
const method = "POST";

const urlBuilder = (base: string, items: Record<string, string>) => {
  let newUrl = baseUrl;
  for (const key in items) {
    newUrl = newUrl.replaceAll(`{${key}}`, items[key]);
  }

  return base + newUrl;
};

export const find_or_create_summary_courses = async (
  token: string,
  baseUrl: string,
  params: {"course_id": string;"topic_id": string;"userInput"?: string},
) => {
  const r = await fetch(urlBuilder(baseUrl, params) + "?per_page=100", {
    method: method,
    headers: {
      Authorization: "Bearer " + token,
    },
  });
  if (r.status != 200) {
    throw r;
  }
  return (await r.json()) as void;
};
