  const baseUrl = "/v1/eportfolios/{eportfolio_id}/pages";
  import type {ePortfolioPage} from "../models/ePortfolioPage"


  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const get_eportfolio_pages = async (token: string, baseUrl: string, params: {
  "eportfolio_id": string
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as ePortfolioPage[]
}
