  const baseUrl = "/quiz/v1/courses/{course_id}/quizzes/{assignment_id}/items/media_upload_url";
  


  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const get_items_media_upload_url = async (token: string, baseUrl: string, params: {
  "course_id": number;"assignment_id": number
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as void
}
