 //Generated by CoasterFan5
  const baseUrl = "/lti/assignments/{assignment_id}";
  import type {LtiAssignment} from "../models/LtiAssignment"


  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const get_single_assignment_lti = async (token: string, baseUrl: string, params: {
  "assignment_id": string;"user_id"?: string
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as LtiAssignment
}
