 //Generated by CoasterFan5
  const baseUrl = "/v1/courses/{course_id}/rubrics/{id}";
  import type {Rubric} from "../models/Rubric"


  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const get_single_rubric_courses = async (token: string, baseUrl: string, params: {
  "course_id": string;"id": string;"include"?: unknown;"style"?: string
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as Rubric
}
