 //Generated by CoasterFan5
  const baseUrl = "/v1/courses/{course_id}/users/{user_id}/progress";
  import type {CourseProgress} from "../models/CourseProgress"


  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const get_user_progress = async (token: string, baseUrl: string, params: {
  "course_id": string;"user_id": string
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as CourseProgress
}
