  const baseUrl = "/v1/announcements";
  import type {DiscussionTopic} from "../models/DiscussionTopic"


  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const list_announcements = async (token: string, baseUrl: string, params: {
  "context_codes": unknown;"start_date"?: undefined;"end_date"?: undefined;"available_after"?: undefined;"active_only"?: boolean;"latest_only"?: boolean;"include"?: unknown
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as DiscussionTopic[]
}
