 //Generated by CoasterFan5
  const baseUrl = "/v1/conferences";
  import type {Conference} from "../models/Conference"


  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const list_conferences_for_current_user = async (token: string, baseUrl: string, params: {
  "state"?: string
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as Conference[]
}
