 //Generated by CoasterFan5
  const baseUrl = "/v1/courses/{course_id}/sections";
  import type {Section} from "../models/Section"


  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const list_course_sections = async (token: string, baseUrl: string, params: {
  "course_id": string;"include"?: unknown;"search_term"?: string
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as Section[]
}
