 //Generated by CoasterFan5
  const baseUrl = "/quiz/v1/courses/{course_id}/quizzes";
  import type {NewQuiz} from "../models/NewQuiz"

  const method = "GET"

  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const list_new_quizzes = async (token: string, baseUrl: string, params: {
  "course_id": number
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    method: method,
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as NewQuiz[]
}
