 //Generated by CoasterFan5
  const baseUrl = "/v1/users/{user_id}/communication_channels/{type}/{address}/notification_preferences";
  import type {NotificationPreference} from "../models/NotificationPreference"


  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const list_preferences_type = async (token: string, baseUrl: string, params: {
  "user_id": string;"type": string;"address": string
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as NotificationPreference[]
}
