 //Generated by CoasterFan5
  const baseUrl = "/v1/courses/{course_id}/quizzes/{quiz_id}/questions";
  import type {QuizQuestion} from "../models/QuizQuestion"


  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const list_questions_in_quiz_or_submission = async (token: string, baseUrl: string, params: {
  "course_id": string;"quiz_id": string;"quiz_submission_id"?: number;"quiz_submission_attempt"?: number
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as QuizQuestion[]
}
