 //Generated by CoasterFan5
  const baseUrl = "/v1/accounts/{account_id}/roles";
  import type {Role} from "../models/Role"


  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const list_roles = async (token: string, baseUrl: string, params: {
  "account_id": string;"state"?: unknown;"show_inherited"?: boolean
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as Role[]
}
