  const baseUrl = "/v1/accounts/{account_id}/csp_settings/lock";
  


  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const lock_or_unlock_current_csp_settings_for_sub_accounts_and_courses = async (token: string, baseUrl: string, params: {
  "account_id": string;"settings_locked": boolean
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as void
}
