  const baseUrl = "/v1/audit/grade_change/graders/{grader_id}";
  import type {GradeChangeEvent} from "../models/GradeChangeEvent"


  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const query_by_grader = async (token: string, baseUrl: string, params: {
  "grader_id": string;"start_time"?: undefined;"end_time"?: undefined
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as GradeChangeEvent[]
}
