 //Generated by CoasterFan5
  const baseUrl = "/v1/groups/{group_id}/folders/by_path";
  import type {Folder} from "../models/Folder"


  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const resolve_path_groups = async (token: string, baseUrl: string, params: {
  "group_id": string
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as Folder[]
}
