 //Generated by CoasterFan5
  const baseUrl = "/v1/eportfolios/{eportfolio_id}/restore";
  import type {ePortfolio} from "../models/ePortfolio"

  const method = "PUT"

  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const restore_deleted_eportfolio = async (token: string, baseUrl: string, params: {
  "eportfolio_id": string
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    method: method,
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as ePortfolio
}
