 //Generated by CoasterFan5
  const baseUrl = "/lti/courses/{course_id}/line_items/{id}";
  import type {LineItem} from "../models/LineItem"


  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const show_line_item = async (token: string, baseUrl: string, params: {
  "course_id": string;"id": string;"include"?: unknown
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as LineItem
}
