  const baseUrl = "/v1/courses/{course_id}/modules/{module_id}/items/{id}";
  import type {ModuleItem} from "../models/ModuleItem"

  const method = "GET"

  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const show_module_item = async (token: string, baseUrl: string, params: {
  "course_id": string;"module_id": string;"id": string;"include"?: unknown;"student_id"?: string
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    method: method,
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as ModuleItem
}
