  const baseUrl = "/v1/users/{user_id}/tokens/{id}";
  


  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const update_access_token = async (token: string, baseUrl: string, params: {
  "user_id": string;"id": string;"token[purpose]"?: string;"token[expires_at]"?: undefined;"token[scopes]"?: unknown;"token[regenerate]"?: boolean
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as void
}
