  const baseUrl = "/v1/folders/{id}";
  import type {Folder} from "../models/Folder"

  const method = "PUT"

  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const update_folder = async (token: string, baseUrl: string, params: {
  "id": string;"name"?: string;"parent_folder_id"?: string;"lock_at"?: undefined;"unlock_at"?: undefined;"locked"?: boolean;"hidden"?: boolean;"position"?: number
}) => {
  const r = await fetch(urlBuilder(baseUrl, params) + "?per_page=100", {
    method: method,
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    throw r
    return undefined
  }
  return (await r.json()) as Folder
}
