  const baseUrl = "/v1/groups/{group_id}/users/{user_id}";
  import type {GroupMembership} from "../models/GroupMembership"


  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const update_membership_users = async (token: string, baseUrl: string, params: {
  "group_id": string;"user_id": string;"workflow_state"?: string;"moderator"?: string
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as GroupMembership
}
