

//IMPORTS
;
const baseUrl = "/v1/users/self/communication_channels/{type}/{address}/notification_preferences";
const method = "PUT";

const urlBuilder = (base: string, items: Record<string, string>) => {
  let newUrl = baseUrl;
  for (const key in items) {
    newUrl = newUrl.replaceAll(`{${key}}`, items[key]);
  }

  return base + newUrl;
};

export const update_multiple_preferences_type = async (
  token: string,
  baseUrl: string,
  params: {"type": string;"address": string;"notification_preferences[<X>][frequency]": string},
) => {
  const r = await fetch(urlBuilder(baseUrl, params) + "?per_page=100", {
    method: method,
    headers: {
      Authorization: "Bearer " + token,
    },
  });
  if (r.status != 200) {
    throw r;
  }
  return (await r.json()) as void;
};
