 //Generated by CoasterFan5
  const baseUrl = "/v1/users/self/communication_channels/{type}/{address}/notification_preferences/{notification}";
  


  const urlBuilder = (base: string, items: Record<string, unknown>) => {
    let newUrl = baseUrl
    for (const key in items) {
      newUrl = newUrl.replaceAll(`{${key}}`, `${items[key]}`)
    }

    return base + newUrl
  }

export const update_preference_type = async (token: string, baseUrl: string, params: {
  "type": string;"address": string;"notification": string;"notification_preferences[frequency]": string
}) => {
  console.log(token)
  const r = await fetch(urlBuilder(baseUrl, params), {
    headers: {
      "Authorization": "Bearer " + token
    }
  })
  if(r.status != 200) {
    console.error(r)
    return undefined
  }
  return (await r.json()) as void
}
